--Reference data tables

CREATE TABLE alert_type
(
    alert_type_id       bigserial   NOT NULL CONSTRAINT alert_type_pk PRIMARY KEY,
    code                varchar(12) NOT NULL,
    description         varchar(40) NOT NULL,
    list_sequence       int,
    created_at          timestamptz NOT NULL,
    created_by          varchar(32) NOT NULL,
    modified_at         timestamptz,
    modified_by         varchar(32),
    deactivated_at      timestamptz,
    deactivated_by      varchar(32)
);

CREATE UNIQUE INDEX idx_alert_type_code ON alert_type (code);
CREATE INDEX idx_alert_type_list_sequence ON alert_type (list_sequence);
CREATE INDEX idx_alert_type_created_at ON alert_type (created_at);
CREATE INDEX idx_alert_type_deactivated_at ON alert_type (deactivated_at);

CREATE TABLE alert_code
(
    alert_code_id       bigserial   NOT NULL CONSTRAINT alert_code_pk PRIMARY KEY,
    alert_type_id       bigserial   NOT NULL REFERENCES alert_type (alert_type_id),
    code                varchar(12) NOT NULL,
    description         varchar(40) NOT NULL,
    list_sequence       int,
    created_at          timestamptz NOT NULL,
    created_by          varchar(32) NOT NULL,
    modified_at         timestamptz,
    modified_by         varchar(32),
    deactivated_at      timestamptz,
    deactivated_by      varchar(32)
);

CREATE INDEX idx_alert_code_alert_type_id ON alert_code (alert_type_id);
CREATE UNIQUE INDEX idx_alert_code_code ON alert_code (code);
CREATE INDEX idx_alert_code_list_sequence ON alert_code (list_sequence);
CREATE INDEX idx_alert_code_created_at ON alert_code (created_at);
CREATE INDEX idx_alert_code_deactivated_at ON alert_code (deactivated_at);

